import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import Favourite from "@material-ui/icons/Favorite";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Hyperlink from "@material-ui/core/Link";
import CardActions from "@material-ui/core/CardActions";
import React from 'react';
import styles from './index.css'; 
import Navbar from './Navbar'
import ReactDOM from "react-dom/client";
import {Link} from 'react-router-dom';
import { useState, useEffect } from "react";
import { CenterFocusStrong, Title } from "@material-ui/icons";
import Popup from './popup';


function App() {
const [data, setData] = useState(null);
 const [loading, setLoading] = useState(true);
 const [error, setError] = useState(null);

 const [isOpen, setIsOpen] = useState(false);

 const togglePopup = () => {
   setIsOpen(!isOpen);
 }

 useEffect(() => {
  const getData = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/products`
      );
      if (!response.ok) {
        throw new Error(
          `This is an HTTP error: The status is ${response.status}`
        );
      }
      let actualData = await response.json();
      setData(actualData);
      setError(null);
    } catch(err) {
      setError(err.message);
      setData(null);
    } finally {
      setLoading(false);
    }  
  }
  getData()
}, [])

  return (
    <div>
      <Navbar></Navbar>
      {loading && <div>A moment please...</div>}
      {error && (
        <div>{`There is a problem fetching the post data - ${error}`}</div>
      )}
      <div style={{display: "flex", flexWrap: "wrap", justifyContent: 'space-evenly', paddingLeft: 30, paddingRight: 30 }}>
      {data &&
          data.map(({ id, title, description, image, amount, rating }) => (
      <Card
        style={{
          height: "15%",
          width: "20%",
          margin: 20,
        }}
        onClick={togglePopup}
      >
        <CardContent>
        <img style={{width: '100%', aspectRatio: 1}}
            src={image}
          />
          <h5 style={{color:'gray'}}>{title}</h5>
          <h5 style={{color:'gray'}}>₹ {amount}</h5>
          <div style={{backgroundColor: 'green', width: 40, textAlign: 'center', color: 'white', borderRadius: 20}}>
          {rating}
          </div>
          <div style={{display: 'flex', justifyContent: 'space-between'}}>
          <Favourite style={{color: 'gray', paddingTop: 30}}/>
          <ShoppingCartIcon style={{color: 'gray', paddingTop: 30}}/>
          </div>
        </CardContent>
        {isOpen && <Popup
      content={<>
        <div style={{display:'flex'}}>
        <img style={{width: '40%', aspectRatio: 1}}
            src={image}
          />
          <div style={{display:'block', padding: 20}}>
           <h2 style={{color:'gray'}}>{title}</h2>
           <h5 style={{color:'gray'}}>{description}</h5>
          <h2 style={{color:'gray'}}>₹ {amount}</h2>
          </div>
        </div>
      </>}
      handleClose={togglePopup}
    />}
      </Card>
      ))}
      </div>
    </div>
  );
}

export default App;
