import Delete from "@material-ui/icons/Cancel";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import React from 'react';
import Navbar from './Navbar'
import { useState, useEffect } from "react";
import IncDecCounter from "./Counter";


function App() {
const [data, setData] = useState(null);
 const [loading, setLoading] = useState(true);
 const [error, setError] = useState(null);

 const [isOpen, setIsOpen] = useState(false);

 useEffect(() => {
  const getData = async () => {
    try {
      const response = await fetch(
        `http://localhost:5000/products`
      );
      if (!response.ok) {
        throw new Error(
          `This is an HTTP error: The status is ${response.status}`
        );
      }
      let actualData = await response.json();
      setData(actualData);
      setError(null);
    } catch(err) {
      setError(err.message);
      setData(null);
    } finally {
      setLoading(false);
    }  
  }
  getData()
}, [])

const togglePopup = () => {
  setIsOpen(!isOpen);

}

  return (
    <div>
      <Navbar></Navbar>
      {loading && <div>A moment please...</div>}
      {error && (
        <div>{`There is a problem fetching the post data - ${error}`}</div>
      )}
      <div style={{backgroundColor: "white", display: "flex", flexWrap: "wrap", justifyContent: 'space-evenly', paddingLeft: 30, paddingRight: 30, width: '50%'}}>
      {data &&
          data.map(({title, description, image, amount, rating }) => (
      <Card
        style={{
          height: 200,
          width: 500,
          margin: 20,
          display: 'flex'
        }}
      >
        <CardContent>
          <div style={{display:'flex'}}>
        <img style={{width: '20%', aspectRatio: 1, padding: 10}}
            src={image}
          />
          <div>
          <h3 style={{color:'gray'}}>{title}</h3>
          <h4 style={{color:'gray'}}>â‚¹ {amount}</h4>
          <IncDecCounter />
          </div>
          <Delete style={{color:'red', paddingTop: 40}}/>
          </div>
        </CardContent>
      </Card>
       ))}
      </div>
    </div>
  );
}

export default App;
